#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    ifstream inFile("file.txt"); // فتح الملف النصي
    string line, word = " "; // تعريف المتغيرات اللازمة لقراءة الملف النصي وحساب التكرارات
    int count = 0; // تعريف المتغير اللازم لحساب عدد الكلمات المتكررة

    while (getline(inFile, line)) {
        for (int i=0; i<line.length(); ++i) {
            char c = line[i];

            if ((c >= 97 && c <= 122) || (c >= 65 && c <= 90)) {
                continue; // تجاهل الحروف الصغيرة والكبيرة العادية
            } else if (word == "" || c =='' || c == '\n') {
                continue; // تجاهل الفراغات والخطوط الجديدة
            } else {
                word += c; // إضافة الحرف الحالي إلى الكلمة الحالية
            }
        }

        if (word!= "") {
            count++; // زيادة العدد إذا كانت هناك كلمة حقيقية
        }

        word = ""; // مسح الكلمة الحالية وإعادة تعيينها لـ" "
    }

    cout << "Number of occurrences: " << count << endl; // طباعة النتيجة النهائية
    return 0;}

