class Stack:
    def __init__(self):
        self.items = []

    def empty(self) -> bool:
        return len(self.items) == 0

    def push(self, item):
        self.items.append(item)

    def pop(self):
        if not self.empty():
            return self.items.pop()

    def peek(self):
        if not self.empty():
            return self.items[-1]

