class Node:
    def __init__(self, data):
        self.data = data
        self.next = None

queue1 = [] # قائمة فارغة لتخزين العناصر التي يتم إضافتها إلى الطابور الأول
queue2 = [] # قائمة فارغة لتخزين العناصر التي يتم إضافتها إلى الطابور الثاني

# إضافة عنصر إلى الطابور 1
def push_to_first_queue(item):
    global queue1
    node = Node(item)
    if len(queue1) == 0:
        queue1.append(node)
    else:
        last_node = queue1[-1]
        while last_node.next is not None:
            last_node = last_node.next

        last_node.next = node

    return item

# إضافة عنصر إلى الطابور 2
def push_to_second_queue(item):
    global queue2
    node = Node(item)
    if len(queue2) == 0:
        queue2.append(node)
    else:
        first_node = queue2[0]
        while first_node.next is not None:
            first_node = first_node.next

        first_node.next = node

    return item

# إزالة العنصر الأول من الطابور 1
def pop_from_first_queue():
    global queue1
    if len(queue1) > 0:
        first_node = queue1[0]
        del queue1[0]
        return first_node.data
    else:
        return None

# إزالة العنصر الأول من الطابور 2
def pop_from_second_queue():
    global queue2
    if len(queue2) > 0:
        first_node = queue2[0]
        del queue2[0]
        return first_node.data
    else:
        return None
