#include <iostream>
using namespace std;

int main() {
    string filename;
    cout << "Enter the filename: ";
    cin >> filename;

    ifstream file(filename);
    if (!file) {
        cout << "Error opening file!" << endl;
        return 0;
    }

    string line;
    int count = 0;
    while (getline(file, line)) {
        if (line.find("word") != string::npos) {
            count++;
        }
    }

    cout << "Number of times word appears in the file: " << count << endl;

    return 0;
