def lcs(s1, s2):
    m = len(s1)
    n = len(s2)

    #initialize dp[][] with all 0 (m+n dimensions), fill only upper triangular part
    dp = [[0 for _ in range(n + 1)] for _ in range(m + 1)]

    #dp[i][j]=length of LCS so far of S1[0...i] & S2[0...j]
    for i in range(m+1):
        for j in range(n+1):
            if i==0 or j==0:
                dp[i][j] = 0
            elif s1[i-1] == s2[j-1]:
                dp[i][j] = dp[i-1][j-1]+1
            else:
                dp[i][j] = max(dp[i-1][j], dp[i][j-1])

    return dp[m][n]
